import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { toast } from 'react-toastify';
import api from '../services/api';
import './CreateMeeting.css';

const CreateMeeting = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    start_time: '',
    end_time: '',
    attendees: '',
    sync_to_calendar: false
  });

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const meetingData = {
      ...formData,
      attendees: formData.attendees
        ? formData.attendees.split(',').map(email => email.trim()).filter(Boolean)
        : []
    };

    try {
      // CSRF cookie is automatically handled by api.js
      await api.post('/meetings', meetingData);
      toast.success('Meeting created successfully!');
      navigate('/meetings');
    } catch (error) {
      console.error('Error creating meeting:', error);
      toast.error(
        error.response?.data?.message ||
        'Failed to create meeting'
      );
    }
  };

  return (
    <div className="create-meeting">
      <div className="create-meeting-container">
        <h2>Create New Meeting</h2>
        <form onSubmit={handleSubmit} className="meeting-form">
          {/* Form fields */}
          <div className="form-group">
            <label>Meeting Title *</label>
            <input type="text" name="title" value={formData.title} onChange={handleChange} required />
          </div>
          <div className="form-group">
            <label>Description</label>
            <textarea name="description" value={formData.description} onChange={handleChange} />
          </div>
          <div className="form-row">
            <div className="form-group">
              <label>Start Time *</label>
              <input type="datetime-local" name="start_time" value={formData.start_time} onChange={handleChange} required />
            </div>
            <div className="form-group">
              <label>End Time *</label>
              <input type="datetime-local" name="end_time" value={formData.end_time} onChange={handleChange} required />
            </div>
          </div>
          <div className="form-group">
            <label>Attendees (comma-separated)</label>
            <input type="text" name="attendees" value={formData.attendees} onChange={handleChange} />
          </div>
          <div className="form-group-checkbox">
            <label>
              <input type="checkbox" name="sync_to_calendar" checked={formData.sync_to_calendar} onChange={handleChange} />
              Sync to Google Calendar
            </label>
          </div>
          <div className="form-actions">
            <button type="button" onClick={() => navigate('/meetings')}>Cancel</button>
            <button type="submit">Create Meeting</button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default CreateMeeting;
